import MoovieBall from "./MoovieBall.vue";
export default {
  data() {
    return {
      xCoord: 0,
      yCoord: 0,
      width: 0,
      height: 0
    };
  },
  components: {
    MoovieBall
  },
  methods: {
    onClick(e) {
      this.xCoord = this.getPreparedX(e);
      this.yCoord = this.getPreparedY(e);
      this.$emit("updateStatistic", {
        x: this.getStatisticX,
        y: this.getStatisticY
      });
    },
    getPreparedX(e) {
      return e.offsetX >= 0 && e.offsetX <= this.width ? e.offsetX : this.xCoord;
    },
    getPreparedY(e) {
      return e.offsetY >= 0 && e.offsetY <= this.height ? e.offsetY : this.yCoord;
    }
  },
  computed: {
    getStatisticX() {
      return this.xCoord - this.width * 0.5 >= 0 ? this.xCoord - this.width * 0.5 : -1 * (this.width * 0.5 - this.xCoord);
    },
    getStatisticY() {
      return this.yCoord - this.height * 0.5 <= 0 ? this.height * 0.5 - this.yCoord : -1 * (this.yCoord - this.height * 0.5);
    }
  },
  mounted() {
    this.$nextTick(() => {
      const element = this.$refs.gridElement;
      this.width = element.offsetWidth;
      this.height = element.offsetHeight;
      this.xCoord = this.width * 0.5;
      this.yCoord = this.height * 0.5;
      this.$emit("updateStatistic", {
        x: this.getStatisticX,
        y: this.getStatisticY
      });
    });
  }
};
